function speech() {
    // Texto del elemento con ID = message
    var text = $('#message').text();
    var msg = new SpeechSynthesisUtterance();

    msg.rate = 0.88;
    msg.pitch = 1;
    msg.text = text;
    msg.lang = 'en-AU';

    msg.onend = function(e) {
        console.log('Finished in ' + event.elapsedTime + ' seconds.');

    };

    speechSynthesis.speak(msg);

};

